import { HttpClientModule } from '@angular/common/http';
import { TestBed } from '@angular/core/testing';
import { FormsModule } from '@angular/forms';
import { By } from '@angular/platform-browser';
import { UserComponent } from './user.component';
describe('UserComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [HttpClientModule, FormsModule],
            declarations: [UserComponent]
        })
            .compileComponents();
    });
    it('should create', () => {
        const fixture = TestBed.createComponent(UserComponent);
        const app = fixture.componentInstance;
        // se espera que ademas de True, sea un componente que exista
        expect(app).toBeTruthy();
    });
    it('Add User', () => {
        // se toma control del componente
        const fixture = TestBed.createComponent(UserComponent);
        const app = fixture.componentInstance;
        //se detectan cambios
        fixture.detectChanges();
        let before = 0;
        for (let user in app.users) {
            i++;
        }
        //se setean la supuesta informacion agregada
        let nombre = app.selectedUser.nombre;
        nombre = "Nombre";
        let primerApellido = app.selectedUser.primerApellido;
        primerApellido = "primerApellido";
        let segundoApellido = app.selectedUser.segundoApellido;
        segundoApellido = "segundoApellido";
        let email = app.selectedUser.email;
        email = "email";
        let telefono = app.selectedUser.telefono;
        telefono = "telefono";
        //se hace accion del boton save
        let btnElement = fixture.debugElement.query(By.css('button.btn-primary'));
        btnElement.nativeElement.click();
        //se crea el resultado esperado
        let testData = 1;
        let i = 0;
        for (let user in app.users) {
            i++;
        }
        //se compara
        expect(i).toEqual(testData);
    });
});
